import React,{useState} from 'react'

const Todo = () => {
    // function cards()
    // {
    //     const inp= document.getElementById("inputtext")
    //     const i=inp.value
    //     let card=document.createElement("div")
    //     card.setAttribute("class","card")
    //     card.setAttribute("id","card_div")
    //     document.getElementById("cards").appendChild(card)
        
    //     let card_header=document.createElement("div")
    //     card_header.setAttribute("class","card-header")
    //     card_header.setAttribute("id","card_head")
    //     card.appendChild(card_header)
        
    //     let header=document.createElement("h4")
    //     header.innerText="User"
    //     card_header.appendChild(header)
        
    //     let body=document.createElement("div")
    //     body.setAttribute("class","card-body")
    //     body.setAttribute("id","card_body")
    //     card.appendChild(body)
        
    //     let checkbox=document.createElement("input")
    //     checkbox.setAttribute("class","form-check-input me-3")
    //     checkbox.setAttribute("id","id_checkbox")
    //     checkbox.setAttribute("type","checkbox")
    //     body.appendChild(checkbox)

    //     let para=document.createElement("p")
    //     para.setAttribute("class","card-text")
    //     para.setAttribute("id","paratext")
    //     para.innerHTML=i
    //     body.appendChild(para)
        
    //     let del=document.createElement("button")
    //     del.setAttribute("class","btn btn-danger mb-2 ms-5 rounded-pill border-dark float-end")
    //     del.setAttribute("id","delete_button")
    //     del.setAttribute("onClick",{dele})
    //     del.innerText="Delete"
    //     body.appendChild(del)
    // }
    const date = new Date();
    const showTime = date.getDate() 
        + "/" +date.getMonth()
        + "/"+date.getFullYear()
        + " "
        + " " + date.getHours()
        + ':' + date.getMinutes();
        + ''
      
    const[todo,setTodo]=useState([])
    const[updateTodo,setupdateTodo]=useState(" ");

    const handleNewTodoChange=(event)=>
    {
        setupdateTodo(event.target.value);
    }
    const formsubmit=(events)=>
    {
        events.preventDefault();
        setTodo([...todo,updateTodo]);
        setupdateTodo('')
    }
    const dele=(index)=>{
        const newTodo=todo.filter((t,i) => i !==index)
        setTodo(newTodo)

    }
    const clear=()=>
    {
        let clear_input=document.getElementById("inputtext")
        if(clear_input.value!==" ")
        {
            clear_input.value=" ";
        }
    }
    // if(todo.length===0)
    // {
    //     todo.push("List is empty")
    // }
        return (
    <>
    <form onSubmit={formsubmit}>
        
    <div className='container' id='first_division' >
        <div className='card'>
            <div className='card-header'>
        <h3>My Day</h3>
        <p id='date'>{showTime}</p>
            </div>
            <div className='card-body' id='inputs'>
        <input type={"text"} placeholder={"Add a Task"} id={"inputtext"} onChange={handleNewTodoChange}/>
        <button id='submit' className='btn btn-primary mt-0 ms-2 pt-1 pb-1' onClick={clear}>Add</button>
            </div>
        </div>
    </div>

    </form>

    <div className='container-fluid' id='inner_div' >
        <h4>To Do's</h4>
    </div>
        <div className='container-fluid' id='main_list'>
    {
            todo.map((currentvalue,index)=>
            (
              <div className='card mt-3' id='card_element'>
                <div className='card-header'>
                    <h3>User</h3>
                </div>
                <div className='card-body' id='elements'>
                    <input type={'checkbox'} id="checkbox" className='ms-1 me-3'></input>
                    <label>{currentvalue}</label>
                    <button className='btn btn-dark float-end btn-outline-danger text-light' onClick={()=>dele(index)}>Delete</button>
                </div>
                </div>
            ))
        }
        </div>
          <div className='container-fluid mt-5' id='complete_div'>
            <h3>Completed To Do's</h3>

          </div>
    </>
  )
}

export default Todo
