import React,{useState} from 'react'

const Todo = () => {
const[text,updatetext]=useState(" ")
const[text2,texts]=useState(text)
const change=(event)=>
{
    updatetext(event.target.value)
}
const display=()=>
{
    texts(text)
}

let d=document.createElement("div")
d.setAttribute("class","card")
d.setAttribute("id","card_div")
document.getElementById("inner_div").appendChild(d)
let d1=document.createElement("div")
d1.setAttribute("class","card-header")
d1.setAttribute("id","card_head")
d.appendChild(d1)

let header=document.createElement("h4")
header.innerHTML="USER"
d1.appendChild(header)

let d2=document.createElement("div")
d2.setAttribute("class","card-body")
d2.setAttribute("id","card_body")
d.appendChild(d2)

let para=document.createElement("p")
para.setAttribute("class","card-text")
para.innerHTML=text2
d2.appendChild(para)
  return (
    <>
    <div className='container-fluid' id='first_division'>
        <h3>My Day</h3>
        <input type={text} placeholder={"Add a Task"} id={"inputtext"} onChange={change}/>
        <button id='submit' className='btn btn-primary mt-0 ms-2 pt-1 pb-1' onClick={display}>Add</button>
    </div>

    <div className='container-fluid' id='inner_div'>
        <h4>Lists</h4>
        </div>
        {/* <div className='card'>
            <div className='card-header mt-5'>
                <h5>{"USER"}</h5>
            </div>
            <div className='card-body'>
                    <p>{text2}</p>
            </div>
    </div> */}
    </>
  )
}

export default Todo
