import React,{useState} from 'react'

const Todo = () => {
const[text,updatetext]=useState(" ")
const[text2,texts]=useState(text)
const change=(event)=>
{
    updatetext(event.target.value)
}
const display=()=>
{
    texts(text)
}
  return (
    <>
    <div className='container-fluid' id='first_division'>
        <h3>My Day</h3>
        <input type={text} placeholder={"Add a Task"} id={"inputtext"} onChange={change}/>
        <button id='submit' className='btn btn-primary mt-0 ms-2 pt-1 pb-1' onClick={display}>Add</button>
    </div>

    <div className='container-fluid' id='inner_div'>
        <h4>Lists</h4>
        </div>
        <div className='card'>
            <div className='card-header mt-5'>
                <h5>{"USER"}</h5>
            </div>
            <div className='card-body'>
                    
            </div>
    </div>
    </>
  )
}

export default Todo
