// card.appendChild(body)

// let para=document.createElement("p")
// para.setAttribute("class","card-text")
// para.innerHTML=text2
// body.appendChild(para)
  return (
    <>
    <div className='container' id='first_division'>
        <div className='card'>
            <div className='card-header'>
        <h3>My Day</h3>
        <p onLoad={dates} id='date'></p>
            </div>
            <div className='card-body'>
        <input type={text} placeholder={"Add a Task"} id={"inputtext"} onLoad={change}/>
        <button id='submit' className='btn btn-primary mt-0 ms-2 pt-1 pb-1' onClick={display}>Add</button>
            </div>

        </div>
    </div>

    <div className='container-fluid' id='inner_div'>
        <h4>Lists</h4>
    </div>
    <div className='container-fluid'>
        <div id='cards' className='container'>
            <h4>{text2}</h4>
        </div>
    </div>
    </>
  )
}

export default Todo
