import React from 'react'
import { useState,useEffect } from 'react'
const Randoms = () => {
    let gen=document.getElementById('passdisplay')
    let btn=document.getElementById("submit")
    let al=document.getElementById("alpha")
    let smallalpha=document.getElementById("smallalpha")
    let num=document.getElementById("num")
    let special=document.getElementById("special")
    let char='ABCDEFGHIJKLMNOPQRSTUVWXYZ'
    let smallchar=char.toLowerCase();
    let number='1234567890'
    let specialchar='!@#$%^&*()_-=+*/|}{[];,.?`~'
    const[password,setPassword]=useState('');
    const[length,setLength]=useState[10];
    const [smallcase,setSmallcase]=useState(false);
    const [largecase,setLargecase]=useState(false);
    const [specialcase,setSpecialcase]=useState(false);
    const [numbers,setNumber]=useState(false);
    const generatepassword=()=>{
        length:length
        lowecase:smallcase
        uppercase:largecase
        num:numbers
        specialcase:specialcase
    };
    set
  return (
    <>
    <div className='container-fluid'>
      <label>Alphabates</label>
      <input type='checkbox' className='mt-3 ms-1 me-5' checked={largecase} id='alpha'></input>
      <label>Small Character</label>
      <input type='checkbox' className='mt-3 ms-1 me-5' checked={smallcase} id='smallalpha'></input>
      <label>Numbers</label>
      <input type='checkbox' className='mt-3 ms-1 me-5' checked={numbers} id='num'></input>
      <label>Special Character</label>
      <input type='checkbox' className='mt-3 ms-1 me-5'checked={specialcase} id='special'></input>
      <label>Select Length of Password</label>
      <input type='number'min={0} max={15} className='mt-3 ms-2 me-5' placeholder='Length'></input>
        <p className='h6'id='passdisplay'></p>
        <button className='btn btn-primary w-50'id='submit' onClick={generatepassword()}>Generate Password</button>
    </div>
    </>
  )
}

export default Randoms
